{
  "name": "Shipment Management System",
  "description": "An enterprise-grade shipment tracking and management dashboard featuring detailed shipment information, history, and action controls."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface ShipmentData {
  shipmentCode: string;
  orderCodes: string[];
  customerCode: string;
  amount: number;
  currency: string;
  dimensions: string;
  weight: number;
  volume: number;
  shippingCost: number;
  carrierName: string;
  shipperName: string;
  trackingNumber: string;
  statusDescription: string;
  note: string;
}

export interface HistoryEntry {
  status: string;
  time: string;
  performedBy: string;
  note: string;
  carrierStatus: string;
  podImageUrl?: string;
}

import React, { useState } from 'react';
import { MOCK_SHIPMENT, MOCK_HISTORY } from './constants';
import { ShipmentData, HistoryEntry } from './types';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState('General information');
  const [shipment, setShipment] = useState<ShipmentData>(MOCK_SHIPMENT);

  // Modal state
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [reDeliveryDate, setReDeliveryDate] = useState('');

  const handleAction = (action: string) => {
    if (action === 'Re-delivered') {
      setIsModalOpen(true);
    } else {
      alert(`Action triggered: ${action}`);
    }
  };

  const handleSaveReDelivery = () => {
    if (!reDeliveryDate) {
      alert('Please select a date and time.');
      return;
    }
    alert(`Re-delivery scheduled for: ${reDeliveryDate}`);
    setIsModalOpen(false);
    setReDeliveryDate('');
