  return (
    <div className="min-h-screen bg-gray-100 pb-10">
      {/* Header */}
      <header className="bg-[#4d9e5f] text-white px-4 h-12 flex items-center justify-between sticky top-0 z-50 shadow-md">
        <div className="flex items-center gap-2 font-semibold text-lg">
          <i className="fa-solid fa-box"></i>
          <span>Shipment Detail</span>
        </div>
        <div className="flex items-center gap-2 text-sm">
          <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center text-[#4d9e5f]">
