          <span>/</span>
          <span className="hover:text-gray-700 cursor-pointer">Shipments</span>
          <span>/</span>
          <span className="text-gray-800 font-medium">Shipment Detail</span>
        </div>
        <button className="text-gray-500 hover:text-gray-800">
          <i className="fa-solid fa-link"></i>
        </button>
      </nav>

      {/* Tabs */}
      <div className="px-4 mt-4">
        <div className="bg-white rounded-t-lg border flex gap-1 p-1">
          {['General information', 'Partner information', 'Items information'].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`px-4 py-2 text-sm font-medium rounded-md transition-colors ${
                activeTab === tab
                  ? 'bg-gray-100 text-[#4d9e5f] border-b-2 border-[#4d9e5f]'
                  : 'text-gray-600 hover:bg-gray-50'
              }`}
            >
              {tab === 'General information' && <i className="fa-solid fa-circle-info mr-2"></i>}
