              onClick={() => handleAction('Re-delivered')}
              className="px-6 py-2 bg-[#4d9e5f] text-white font-bold rounded shadow-sm hover:bg-[#3d7d4c] transition-all flex items-center gap-2"
            >
              <i className="fa-solid fa-rotate-right"></i>
              Re-delivered
            </button>

            {/* Action: Return to warehouse */}
            <button 
              onClick={() => handleAction('Return to warehouse')}
              className="px-6 py-2 bg-orange-500 text-white font-bold rounded shadow-sm hover:bg-orange-600 transition-all flex items-center gap-2"
            >
              <i className="fa-solid fa-warehouse"></i>
              Return to warehouse
            </button>
          </div>
        </div>

        {/* Content Section */}
        <div className="bg-white border border-t-0 rounded-b-lg p-6 space-y-8">
          {/* Shipment Information */}
          <section>
