const InfoRow: React.FC<{ label: string; value: React.ReactNode; bold?: boolean; fullWidth?: boolean }> = ({ label, value, bold, fullWidth }) => {
  if (label === 'Empty') return <div className="bg-white border-b border-r last:border-r-0 min-h-[44px]"></div>;

  return (
    <div className={`flex items-center min-h-[44px] border-b border-r last:border-r-0 ${fullWidth ? 'md:col-span-3' : ''}`}>
      <div className="w-1/3 md:w-40 bg-[#f8f9fa] px-4 h-full flex items-center border-r font-bold text-gray-700 text-xs">
        {label}
      </div>
      <div className={`flex-1 px-4 text-sm ${bold ? 'font-bold' : ''}`}>
        {value}
      </div>
    </div>
  );
};

export default App;

