              <button 
                onClick={() => setIsModalOpen(false)}
                className="px-4 py-2 text-sm font-semibold text-gray-500 hover:text-gray-800 transition"
              >
                Cancel
              </button>
              <button 
                onClick={handleSaveReDelivery}
                className="px-6 py-2 bg-[#4d9e5f] text-white font-bold rounded-lg shadow-md hover:bg-[#3d7d4c] active:scale-95 transition-all flex items-center gap-2"
              >
                <i className="fa-solid fa-floppy-disk"></i>
                Save Schedule
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const InfoRow: React.FC<{ label: string; value: React.ReactNode; bold?: boolean; fullWidth?: boolean }> = ({ label, value, bold, fullWidth }) => {
  if (label === 'Empty') return <div className="bg-white border-b border-r last:border-r-0 min-h-[44px]"></div>;

  return (
    <div className={`flex items-center min-h-[44px] border-b border-r last:border-r-0 ${fullWidth ? 'md:col-span-3' : ''}`}>
      <div className="w-1/3 md:w-40 bg-[#f8f9fa] px-4 h-full flex items-center border-r font-bold text-gray-700 text-xs shrink-0">
        {label}
      </div>
      <div className={`flex-1 px-4 text-sm ${bold ? 'font-bold' : ''}`}>
        {value}
      </div>
    </div>
  );
};

