    if (newStatus === 'Returning Local Hub') {
      setSelectedShipmentId(id);
      setIsReturningLocalHubModalOpen(true);
    } else {
      setListShipments(prev => prev.map(s => s.id === id ? { ...s, status: newStatus } : s));
    }
