    if (selectedShipmentId) {
      setListShipments(prev => prev.map(s => s.id === selectedShipmentId ? { ...s, status: 'Returned Local Hub' } : s));
      setIsReturningLocalHubModalOpen(false);
      setSelectedShipmentId(null);
