        </div>
        <nav className="flex-1 py-4 overflow-y-auto">
          <SidebarItem icon="fa-gauge" label="Dashboard" onClick={() => {}} />
          <SidebarItem icon="fa-box" label="Orders" onClick={() => {}} />
