const FormSelect: React.FC<{ 
  label: string; 
  required?: boolean; 
  value?: string; 
  onChange: (val: string) => void;
  options: string[] | { label: string; value: string }[];
}> = ({ label, required, value, onChange, options }) => (
  <div className="space-y-1">
